perspectives.style:
@controls {
  mode: normal;
  toolbar: click;

  top {
    title {
      value: "台灣罕見疾病基金會國內遺傳檢驗補助行動者網絡圖";
    }

    filter {
      font-size: 15;
      font-weight: 700;
      as: label;
      default: show-all;
      multiple: true;
      placeholder: "Layer Menu";

      option {
        label: "全圖";
        selector: *;
      }

      option {
        label: "核心";
        selector: ["tags"*="core"], connection;
      }

      option {
        label: "政府";
        selector: ["tags"*="gov"], connection;
      }

      option {
        label: "外圍";
        selector: ["tags"*="ext"], connection;
      }
    }
  }
}

@settings {
  template: stock-and-flow;
}

["Element Type"="actor gta"] {
  color: gold;
}

["Element Type"="actor tfrd"] {
  color: skyblue;
}

["Element Type"="actor hospital"] {
  color: lightgreen;
}

